void Screen1ViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &ExitCfg)
    {
        //Interaction1
        //When ExitCfg clicked hide container1
        //Hide container1
        container1.setVisible(false);
        container1.invalidate();
    }
    if (&src == &bSignal2)
    {
        //Interaction3
        //When bSignal2 clicked call virtual function
        //Call Signal2_cfg
        Signal2_cfg();
    }
    if (&src == &bSignal3)
    {
        //Interaction4
        //When bSignal3 clicked call virtual function
        //Call Signal3_cfg
        Signal3_cfg();
    }
    if (&src == &fft)
    {
        //Interaction6
        //When fft clicked change screen to Screen2
        //Go to Screen2 with screen transition towards East
        application().gotoScreen2ScreenSlideTransitionEast();
    }
}

void Screen1ViewBase::sliderValueChangedCallbackHandler(const touchgfx::Slider& src, int value)
{
    if (&src == &Freq1Slider)
    {
        //Interaction7
        //When Freq1Slider value changed call virtual function
        //Call SliderFreqCalback
        SliderFreqCalback(value);
    }
    if (&src == &slider1_2)
    {
        //Interaction8
        //When slider1_2 value changed call virtual function
        //Call AmplitudeCalback
        AmplitudeCalback(value);
    }
}
